var app=new Vue({el:"#app",data:{hour:0,minute:0,standard:7.25,result_d:0,result_h:0,result_m:0,seen:false,history:[]},methods:{gettime:function(){var f=Math.floor(this.hour*60),g=Math.floor(this.minute),h=(f+g)/(this.standard*60),j;var i=this.checkdata();if(!i){return false}this.result_d=Math.floor(h);j=(f+g)-(this.result_d*this.standard*60);this.result_h=Math.floor(j/60);this.result_m=Math.floor(j%60);this.history.push(this.result_d+" 天 "+this.result_h+" 小时 "+this.result_m+" 分钟")},checkdata:function(){if(parseInt(this.hour)!=parseFloat(this.hour)||parseInt(this.minute)!=parseFloat(this.minute)){this.result_d=0;this.result_h=0;this.result_m=0;this.seen=true;return false}this.seen=false;return true}}});