var app=new Vue({el:"#app",data:{hour:0,minute:0,standard:7.25,result_d:0,result_h:0,result_m:0,result_ds:0,result_hs:0,result_ms:0,seen:false,history:[],hours:0,minutes:0,count:0},methods:{gettime:function(){var b=this.convert(this.hour,this.minute);this.result_d=b[0];this.result_h=b[1];this.result_m=b[2];this.addhistory();this.getsum()},convert:function(l,j){var p=Math.floor(l*60),q=Math.floor(j),r=(p+q)/(this.standard*60),t,s,o,n,k;s=this.checkdata();if(!s){return false}o=Math.floor(r);t=(p+q)-(o*this.standard*60);n=Math.floor(t/60);k=Math.floor(t%60);return[o,n,k]},addhistory:function(){this.hours+=parseInt(this.hour);this.minutes+=parseInt(this.minute);this.history.unshift({input:parseInt(this.hour)+" 小时 "+parseInt(this.minute)+" 分钟",text:this.result_d+" 天 "+this.result_h+" 小时 "+this.result_m+" 分钟"});this.count=this.history.length},checkdata:function(){if(parseInt(this.hour)!=parseFloat(this.hour)||parseInt(this.minute)!=parseFloat(this.minute)){this.result_d=0;this.result_h=0;this.result_m=0;this.seen=true;return false}this.seen=false;return true},clearhistory:function(){this.hour=0;this.minute=0;this.count=0;this.hours=0;this.minutes=0;this.history=[];this.getsum()},getsum:function(){var b=this.convert(this.hours,this.minutes);this.result_ds=b[0];this.result_hs=b[1];this.result_ms=b[2]}}});