var app=new Vue({el:"#app",data:{hour:0,minute:0,standard:7.25,result_d:0,result_h:0,result_m:0,seen:false,history:[]},methods:{gettime:function(){var e=Math.floor(this.hour*60),d=Math.floor(this.minute),c=(e+d)/(this.standard*60),a;var b=this.checkdata();if(!b){return false}this.result_d=Math.floor(c);a=(e+d)-(this.result_d*this.standard*60);this.result_h=Math.floor(a/60);this.result_m=Math.floor(a%60);this.history.unshift({input:parseInt(this.hour)+" 小时 "+parseInt(this.minute)+" 分钟",text:this.result_d+" 天 "+this.result_h+" 小时 "+this.result_m+" 分钟"})},checkdata:function(){if(parseInt(this.hour)!=parseFloat(this.hour)||parseInt(this.minute)!=parseFloat(this.minute)){this.result_d=0;this.result_h=0;this.result_m=0;this.seen=true;return false}this.seen=false;return true},clearhistory:function(){this.hour=0;this.minute=0;this.history=[]}}});